mapper.update:
update M_DEVICEINFO
userName=#{userName,jdbcType=VARCHAR}, wifiIp=#{wifiIp,jdbcType=VARCHAR}, language=#{language,jdbcType=VARCHAR}, idfa=#{idfa,jdbcType=VARCHAR}, idfv=#{idfv,jdbcType=VARCHAR}, imei=#{imei,jdbcType=VARCHAR}, imsi=#{imsi,jdbcType=VARCHAR}, operaDesc=#{operaDesc,jdbcType=VARCHAR}, appVersion=#{appVersion,jdbcType=VARCHAR}, resolution=#{resolution,jdbcType=VARCHAR}, systemType=#{systemType,jdbcType=VARCHAR}, isRooted=#{isRooted,jdbcType=VARCHAR}, wifiName=#{wifiName,jdbcType=VARCHAR}, networkType=#{networkType,jdbcType=VARCHAR}, carrier=#{carrier,jdbcType=VARCHAR}, cellIp=#{cellIp,jdbcType=VARCHAR}, regId=#{regId,jdbcType=VARCHAR}, systemVersion=#{systemVersion,jdbcType=VARCHAR}, wifiList=#{wifiList,jdbcType=VARCHAR}, phoneNumber=#{phoneNumber,jdbcType=VARCHAR}, locInfo=#{locInfo,jdbcType=VARCHAR}, xm=#{xm,jdbcType=VARCHAR}, lastLoginTime=#{lastLoginTime,jdbcType=TIMESTAMP},
where userName = #{userName}

mapper.select:
select count(1) from xg_rcsw_qjgl_qjjg t
where to_date(t.kssj, 'yyyy-MM-dd HH24:mi:ss') < to_date(#{time}, 'yyyy-MM-dd HH24:mi:ss') and to_date(#{time}, 'yyyy-MM-dd HH24:mi:ss') < to_date(t.jssj, 'yyyy-MM-dd HH24:mi:ss')
and t.xh = #{username}
